$(document).ready(function(){
	$(".mobile").click(function(){ $(".list").toggleClass("responsive")});
	$(".mobile").click(function(){ $(".navigation").css("align-items", "flex-start")});
	$(".mobile2").click(function(){ $(".list_center").toggleClass("responsive")});

	$(".button_black").click(function(){ $(".moreInfo").slideToggle(800)});

	$(".ourServices__images .images__wrap").css("margin-bottom","17px");

	let value = $(".team__procent > span").text();
	let arr = value.split("%");
	let width = $(".myBar");
	for (let i = 0; i < width.length; i++) {
		width.get(i).style.width = $.trim(arr[i])+"%";
	}

	$(".blogNews__link").click(function(){ $(".blogMoreInfo").slideToggle(800)});
})