$(document).ready(function(){
	$(".navMenu__mobile").click(function(){ $(".list").toggleClass("responsive")});
	$(".navMenu__mobile").click(function(){ $(".navigation").css("align-items", "flex-start")});
	$(".navMenu__mobile2").click(function(){ $(".list_center").toggleClass("responsive")});

	$(".button_black").click(function(){ $(".moreInfo").slideToggle(800)});

	$(".ourServices__images .images__wrap").css("margin-bottom","17px");

	let e = ["80%","65%","75%","70%","60%","85%","90%","45%","85%"];
	let c = $(".myBar");
	for (let i = 0; i < c.length; i++) {
		c.get(i).style.width = e[i];
	}

	$(".blogNews__link").click(function(){ $(".blogMoreInfo").slideToggle(800)});
})